mapper.select:
 SELECT * FROM board_vote 
  WHERE board_seq = #{boardSeq}
	AND board_type_seq = #{boardTypeSeq}
	AND member_seq = #{memberSeq}

mapper.delete:
 DELETE FROM board_vote 
  WHERE board_seq = #{boardSeq}
	AND board_type_seq = #{boardTypeSeq}
	AND member_seq = #{memberSeq}
	AND is_like = #{isLike}

mapper.update:
 UPDATE board_vote
	SET is_like = #{isLike},
		ip = #{ip}
  WHERE board_seq = #{boardSeq}
	AND board_type_seq = #{boardTypeSeq}
	AND member_seq = #{memberSeq}

mapper.delete:
 DELETE FROM board 
  WHERE board_seq = #{boardSeq}
	AND board_type_seq = #{boardTypeSeq}

mapper.select:
 SELECT COUNT(*) 
   FROM forum.board_vote
  WHERE is_like = 'Y'
	AND board_seq = #{boardSeq}
	AND board_type_seq = #{boardTypeSeq}

 SELECT COUNT(*) 
   FROM forum.board_vote
  WHERE is_like = 'N'
	AND board_seq = #{boardSeq}
	AND board_type_seq = #{boardTypeSeq}

mapper.update:
UPDATE forum.board 
   SET hit = hit + 1 
 WHERE board_seq = #{boardSeq}
   AND board_type_seq = #{boardTypeSeq}
